// SEND THE DATA TO CONTROL OF LOGIC
    private void btnSaveActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnSaveActionPerformed
        String selectedA = (String) cmbAlergic.getSelectedItem();
        String selectedS = (String) cmbSpeAtention.getSelectedItem();
        boolean alergic = false;
        boolean speAtention = false;
        
        if(selectedA=="Yes"){
            alergic=true;
        }else if(selectedA=="No"){
            alergic=false;
        }
        
        if(selectedS=="Yes"){
            speAtention=true;
        }else if(selectedS=="No"){
            speAtention=false;
        }
        
        col.save(txtName.getText(), txtBreed.getText(), txtColor.getText(), txtObservations.getText(), txtOwName.getText(), txtOwPhone.getText(), alergic, speAtention);
        
        JOptionPane optionPane = new JOptionPane("Your Data was saved!");
        optionPane.setMessageType(JOptionPane.INFORMATION_MESSAGE);
        JDialog dialog = optionPane.createDialog("Success Save");
        dialog.setAlwaysOnTop(true);
        dialog.setVisible(true);
        cleanData();
    }//GEN-LAST:event_btnSaveActionPerformed

