    private void cleanData(){
        txtName.setText("");
        txtBreed.setText("");
        txtColor.setText("");
        txtObservations.setText("");
        txtOwName.setText("");
        txtOwPhone.setText("");
        cmbAlergic.setSelectedIndex(0);
        cmbSpeAtention.setSelectedIndex(0);
    }
    
